pragma solidity >=0.8.4;

interface IContentHashResolver {
  event ContenthashChanged(bytes32 indexed node, bytes hash);

  /**
   * Returns the contenthash associated with an ENS node.
   * @param node The ENS node to query.
   * @return The associated contenthash.
   */
  function contenthash(bytes32 node) external view returns (bytes memory);
}
","keccak256":"0x43e90f6c6f3ba68d48bc98c3d2d5b26d8ca1047dfbf074f6a4cb88f77dca396d","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/IDNSRecordResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface IDNSRecordResolver {
  // DNSRecordChanged is emitted whenever a given node/name/resource's RRSET is updated.
  event DNSRecordChanged(bytes32 indexed node, bytes name, uint16 resource, bytes record);
  // DNSRecordDeleted is emitted whenever a given node/name/resource's RRSET is deleted.
  event DNSRecordDeleted(bytes32 indexed node, bytes name, uint16 resource);

  /**
   * Obtain a DNS record.
   * @param node the namehash of the node for which to fetch the record
   * @param name the keccak-256 hash of the fully-qualified name for which to fetch the record
   * @param resource the ID of the resource as per https://en.wikipedia.org/wiki/List_of_DNS_record_types
   * @return the DNS record in wire format if present, otherwise empty
   */
  function dnsRecord(bytes32 node, bytes32 name, uint16 resource) external view returns (bytes memory);
}
","keccak256":"0xf01e1226dc438f32be21a5feb2528942dffecd96ab0c4eb1a582c476050d4f4d","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/IDNSZoneResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface IDNSZoneResolver {
  // DNSZonehashChanged is emitted whenever a given node's zone hash is updated.
  event DNSZonehashChanged(bytes32 indexed node, bytes lastzonehash, bytes zonehash);

  /**
   * zonehash obtains the hash for the zone.
   * @param node The ENS node to query.
   * @return The associated contenthash.
   */
  function zonehash(bytes32 node) external view returns (bytes memory);
}
