interface IExtendedResolver {
  function resolve(bytes memory name, bytes memory data) external view returns (bytes memory);
}
","keccak256":"0x603c4bd5b9219a7d6a6248e7687a848966bf27c3dc10ca2315f168d8e069d804","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/IInterfaceResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface IInterfaceResolver {
  event InterfaceChanged(bytes32 indexed node, bytes4 indexed interfaceID, address implementer);

  /**
   * Returns the address of a contract that implements the specified interface for this name.
   * If an implementer has not been set for this interfaceID and name, the resolver will query
   * the contract at `addr()`. If `addr()` is set, a contract exists at that address, and that
   * contract implements EIP165 and returns `true` for the specified interfaceID, its address
   * will be returned.
   * @param node The ENS node to query.
   * @param interfaceID The EIP 165 interface ID to check for.
   * @return The address that implements this interface, or 0 if the interface is unsupported.
   */
  function interfaceImplementer(bytes32 node, bytes4 interfaceID) external view returns (address);
}
","keccak256":"0xbbe45357ac199d06362207c454ffa7580bd187b13007aea9c1f061da4e99c45d","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/INameResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface INameResolver {
  event NameChanged(bytes32 indexed node, string name);

  /**
   * Returns the name associated with an ENS node, for reverse records.
   * Defined in EIP181.
   * @param node The ENS node to query.
   * @return The associated name.
   */
  function name(bytes32 node) external view returns (string memory);
}
","keccak256":"0xce8c74368be656cc2880b1ba3147198eeb36f244d0887b16caf4fe5eef7438b7","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/IPubkeyResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface IPubkeyResolver {
  event PubkeyChanged(bytes32 indexed node, bytes32 x, bytes32 y);

  /**
   * Returns the SECP256k1 public key associated with an ENS node.
   * Defined in EIP 619.
   * @param node The ENS node to query
   * @return x The X coordinate of the curve point for the public key.
   * @return y The Y coordinate of the curve point for the public key.
   */
  function pubkey(bytes32 node) external view returns (bytes32 x, bytes32 y);
}
","keccak256":"0x32cf06260cfe504394b1ee106a403ead28eb649ed36c8ef4047ef5979f90b21c","license":"MIT"},"@ensdomains/ens-contracts/contracts/resolvers/profiles/ITextResolver.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.4;

interface ITextResolver {
  event TextChanged(bytes32 indexed node, string indexed indexedKey, string key, string value);

  /**
   * Returns the text data associated with an ENS node and key.
   * @param node The ENS node to query.
   * @param key The text data key to query.
   * @return The associated text data.
   */
  function text(bytes32 node, string calldata key) external view returns (string memory);
}
